const Success=()=>{

const location=useLocation();

const [userData,setUserData]=useState('');

//fetch data passed from home using state in history.push 
useEffect(() => {
    setUserData(location.state.user)
}, [location])

return(
    <>
    {location?<Jumbotron>
    <h1>You have registered successfully</h1>
    <p>
        Your registration details are:
        <p className="modalText">
           <h4><span>Registration ID:</span>{userData._id}</h4>
            {/* <h4><span>Organisation Name:</span>{orgname}</h4>
              <h4><span>Phone:</span>{phone!==undefined||phone!==null?phone:`  Add Phone No.`}</h4>
