import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button,Form,Modal } from 'react-bootstrap';

import {useState} from 'react'
import {BrowserRouter,Route,Switch,useHistory} from 'react-router-dom'


const Home=()=>{
    const [name,setName]=useState("");
    const [orgname,setOrgName]=useState("");
    const [eid,seteId]=useState("");
    const [phone,setPhone]=useState("");
    const [email,setEmail]=useState("");
    const [photo,setPhoto]=useState("");
    const [show, setShow] = useState(false);
    const [previewImg,setPreviewImg]=useState('');
    const [validated, setValidated] = useState(false);
    const history=useHistory();




    const uploadImage = (e) =>
    {
          e.preventDefault();
          if(!photo)
          {
            alert("Upload a user image!");
            return;
          }
          // const formData=new FormData();
          // 
          // formData.append('userImage',photo);
          // fetch("http://localhost:5000/uploadImage",
          // {
          //   method:'POST',
          //   body:formData

          // }).then(res=>res.json())
          // .then(res=>
          //   {
          //       console.log(res.message);
          //       setPhoto(res.imagePath);
          //   })
          // .catch(err=>console.log(err.message))
          setPreviewImg(URL.createObjectURL(photo));
          alert("Image Uploaded");

    }


  const showModal = (event) => {

    const form = event.currentTarget;
    if (form.checkValidity() === false || photo==='') {
      if(photo=='')
      {
        alert("Upload user image");
        return;
      }
      event.preventDefault();
      event.stopPropagation();
    }
    else
    {
      event.preventDefault();
      console.log(previewImg)
      setShow(true);
    }
      setValidated(true);
  };


    const postform=()=>{
        alert("done")

        // fetch('/hi',{
        //     method:"post",
        //     headers:{
        //         "Content-Type":"application/json"
        //     },
        //     body:JSON.stringify({
        //         name,
        //         email,
        //         eid,
        //         orgname,
        //         phone,
        //         photo
        //     })
        // }).then(res=>res.json())
        // .then(data=>{
            history.push('/success')
        // })
    }

    return (
      <div className="App">

       <Form 
       style={{width:"70%",margin:"2% auto",padding:"2% 5%",border:"1px",borderRadius:"1rem",background:"#f8f1f1"}}
       noValidate validated={validated}
       onSubmit={showModal} >


       <h3>Add Details </h3>


       <Form.Group controlId="formBasicName">

          <Form.Label >Name</Form.Label>

          <Form.Control type="text" 
          placeholder="Enter full name" 
          onChange={(e)=>setName(e.target.value)} 
          required/>


          <Form.Control.Feedback type="invalid">Please enter your full name.</Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>

        </Form.Group>



        <Form.Group controlId="formBasicOrganisation">

<Form.Label >Organisation Name</Form.Label>

<Form.Control type="text" 
placeholder="Enter organisation name" 
onChange={(event)=>setOrgName(event.target.value)} 
required/>

<Form.Control.Feedback type="invalid">Please enter your organisation name.</Form.Control.Feedback>
<Form.Control.Feedback>Looks good!</Form.Control.Feedback>

</Form.Group>





<Form.Group controlId="formBasicId">

<Form.Label >Emplopyee ID No.</Form.Label>

<Form.Control type="number" 
placeholder="Enter Emplopyee ID No" 
onChange={(event)=>seteId(event.target.value)} 
required/>

<Form.Control.Feedback type="invalid">Please enter your employee ID No.</Form.Control.Feedback>
<Form.Control.Feedback>Looks good!</Form.Control.Feedback>


</Form.Group>




<Form.Group controlId="formBasicPhone">

<Form.Label >Phone No.</Form.Label>

<Form.Control type="number" 
placeholder="Enter Phone No." 
onChange={(event)=>setPhone(event.target.value)} required/>

<Form.Control.Feedback type="invalid">Please enter a valid phone number.</Form.Control.Feedback>
<Form.Control.Feedback>Looks good!</Form.Control.Feedback>


</Form.Group>


<Form.Group controlId="formBasicEmail">

<Form.Label>Email address</Form.Label>

<Form.Control type="email" 
placeholder="Enter email" 
onChange={(event)=>setEmail(event.target.value)} 
required/>

<Form.Text className="text-muted">
  We'll never share your email with anyone else.
</Form.Text>


<Form.Control.Feedback type="invalid">Please enter your correct email ID.</Form.Control.Feedback>
<Form.Control.Feedback>Looks good!</Form.Control.Feedback>


</Form.Group>

<Form.Group>

<Form.Label>Upload ID Card</Form.Label>

<Form.File id="exampleFormControlFile1" 
onChange={(event)=>{setPhoto(event.target.files[0])}} 
accept=".jpg,.png,.jpeg" 
required/>       


<Form.Control.Feedback type="invalid">Image required!</Form.Control.Feedback>
<Form.Control.Feedback>Looks good!</Form.Control.Feedback>

</Form.Group>

<Button type="button" variant="primary" onClick={uploadImage}>Upload Image</Button>

<br/>
<br/>

        <Button variant="primary" type="submit">
          Submit
        </Button>

        </Form>






        <Modal
          className="modal"
          show={show}
          onHide={() => setShow(false)}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-custom-modal-styling-title">
              Check your Details
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>
              <img src={previewImg} style={{width:"20%",height:"20%",borderRadius:"50%"}}/>
              <label>Name</label>:<h3>{name}</h3>
              <label>Name</label><h3>Organisation Name:{orgname}</h3>
              <label>Name</label><h3>Phone:{phone!==undefined||phone!==null?phone:`  Add Phone No.`}</h3>
              <label>Name</label><h3>Employee ID:{eid}</h3>
              <label>Name</label><h3>{email}</h3>
              <div style={{display:'flex',justifyContent:'space-between'}}>
              <Button onClick={postform}>Submit</Button>
              <Button onClick={()=>setShow(false)}>Make Changes</Button>
              </div>
            </p>
          </Modal.Body>
        </Modal>


      </div>
    );
}

export default Home
