import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button,Form,Modal } from 'react-bootstrap';

import {useState} from 'react'
import {BrowserRouter,Route,Switch,useHistory} from 'react-router-dom'


const Home=()=>{
    const [name,setName]=useState("");
    const [orgname,setOrgName]=useState("");
    const [eid,seteId]=useState("");
    const [phone,setPhone]=useState("");
    const [email,setEmail]=useState("");
    const [photo,setphoto]=useState("");
    const [show, setShow] = useState(false);
    const history=useHistory()


  const [validated, setValidated] = useState(false);

  const showModal = (event) => {

    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    }
    else
    {
      event.preventDefault();
      setShow(true);
    }
      setValidated(true);
  };


    const postform=()=>{
        alert("done")

        // fetch('/hi',{
        //     method:"post",
        //     headers:{
        //         "Content-Type":"application/json"
        //     },
        //     body:JSON.stringify({
        //         name,
        //         email,
        //         eid,
        //         orgname,
        //         phone,
        //         photo
        //     })
        // }).then(res=>res.json())
        // .then(data=>{
            history.push('/success')
        // })
    }

    return (
      <div className="App">

       <Form 
       style={{width:"70%",margin:"2% auto",padding:"2% 5%",border:"1px",borderRadius:"1rem",background:"#f8f1f1"}}
       noValidate validated={validated}
       onSubmit={showModal} >


       <h3>Add Details </h3>


       <Form.Group controlId="formBasicName">

          <Form.Label >Name</Form.Label>

          <Form.Control type="text" 
          placeholder="Enter full name" 
          onChange={(e)=>setName(e.target.value)} 
          required/>


          <Form.Control.Feedback type="invalid">Please enter your full name.</Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>

        </Form.Group>




        <Button variant="primary" type="submit">
          Submit
        </Button>

        </Form>






        <Modal
          show={show}
          onHide={() => setShow(false)}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title"
        >
          <Modal.Header closeButton>
            <Modal.Title id="example-custom-modal-styling-title">
              Check your Details
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>
              <h3>Name:{name}</h3>
              <h3>Organisation Name:{orgname}</h3>
              <h3>Phone:{phone!==undefined||phone!==null?phone:`  Add Phone No.`}</h3>
              <h3>Employee ID:{eid}</h3>
              <h3>Email ID:{email}</h3>
              <Button onClick={()=>postform()}>Submit</Button>
              <Button onClick={()=>setShow(false)}>Make Changes</Button>
            </p>
          </Modal.Body>
        </Modal>


      </div>
    );
}

export default Home
