import React from 'react'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'



const Cart = ({items}) => {



    const CartItem = () =>


    items.length ?
    (  
        items.map(item=>{
            return(

                <li className="collection-item avatar" key={item.objectId}>
                <div className="item-img"> 
                <img src={item.ItemImage.url} alt={item.ItemName} className=""/>
                </div>
                <div className="item-desc">
                    <span className="title">{item.title}</span>
                                <h4>{item.ItemName}</h4>
                                <p><b>Price: Rs{item.Price}</b></p> 
                                <p>
                                    <b>Quantity: {item.quantity}</b> 
                                </p>
                                <div className="add-remove">
                                    <Link to="/cart"><i className="material-icons">arrow_drop_up</i></Link>
                                    <Link to="/cart"><i className="material-icons">arrow_drop_down</i></Link>
                                </div>
                                <button className="waves-effect waves-light btn pink remove">Remove</button>
                            </div>

                       </li>                        
            )
        })
    ):

     (
        <p>Nothing.</p>
     )


    return (
        <div className="container">
        <div className="cart">
            <h5>You have ordered:</h5>
            <ul className="collection">
               <CartItem/>
            </ul>
        </div>  
    </div>
    )
}

const mapStateToProps = (state)=>{
    return{
        items: state.addedItems
    }
}

export default connect(mapStateToProps)(Cart)
