import React,{useEffect} from 'react'
import { Link } from 'react-router-dom'
import '../Home/homeStyles.css'

import blackcrockery from '../images/blackcrockery.png'
import knifEorange from '../images/knifEorange.jpeg'






const Navbar = () => {


useEffect(() => {
    window.addEventListener('scroll',function()
    {
        const header=document.querySelector('header');
        const image=document.querySelector('.img-logo');
        header.classList.toggle('sticky',window.scrollY>0);
        if(header.classList.contains('sticky'))
        {
            image.src=knifEorange;
            image.id=''
        }
        else
        {
            image.src=blackcrockery;
            image.id='invertLogo'
        }

    })

    toggleMenu();


}, [])




const toggleMenu = () =>
{
    const menu=document.querySelector('.toggle');
    const nav=document.querySelector('.navigation');
    menu.classList.toggle('active')
    nav.classList.toggle('active')
}


    return (
<header className="header">
    <a href="/" className="logo">OfficeEats
    <span><img src={blackcrockery} className="img-logo" id="invertLogo"></img></span></a>


   {/* toggler  */}

   <div className="toggle"
   onClick={toggleMenu}> </div>


    <ul class="navigation">
       <Link to="/"><li ><a href="#banner">Home</a></li></Link>
       <Link to="/about"><li><a href="#about">About Us</a></li></Link>
       <Link to="/menu"><li><a href="#menu">Menu</a></li></Link>
       <Link to="/home"><li><a href="/addDetails">Order Now</a></li></Link>
       <Link to="/home"><li><a href="/" id="last-li">Login</a></li></Link>
       <hr/>
    </ul>
</header>
    )
}

export default Navbar
