import React,{useEffect, useState} from "react";
import LoadingOverlay from 'react-loading-overlay';
import { Card, CardDeck, Col, Container,Row,Button, Spinner} from "react-bootstrap";

import CafeNav from "./CafeNav";

const Cafepage = () => {


  const [foodItems,setFoodItems]=useState('');
  const [total,setTotal]=useState(0);

  useEffect(() => {
    fetch("http://localhost:5000/cafe/foodList",
    {
      method:'POST'
    })
    .then(res=>res.json())
    .then(res=>{
      console.log(res.items)
      setFoodItems(res.items)
    })
    .catch(err=>console.log(err))

  }, [])





const [cart,setCart]=useState([]);
const [loading,setLoading]=useState(false)

const addToCart = (name,price) =>
{
   console.log(name,price)
  //  cart.push({name,price})
   setCart([...cart,{name,price}])
   setTotal(total+price);

}

const Spin = () =>
{
   return (
    <div>
      <LoadingOverlay
      active={loading}
      spinner
      text='Loading your content...'
      >
    </LoadingOverlay>
     </div>
   )
}

const pay = () =>
{
   setLoading(true);
   alert('Payment Processing!')
}
  //  console.log(JSON.parse(localStorage.getItem('cart')))

  return (
    <>
      <CafeNav
      //   eid={eid} eimage={eimage}
      />

        <Container style={{margin:'10px auto'}}>
          <Row>
            <Col xs={9}>
            <Row>
              {foodItems!='' && foodItems.map((item)=>
                {
                  return(
              <Col xs={5}>
              <Card style={{marginBottom:'20px'}}>
             <Card.Img
             className="cardImage"
               variant="top"
               src={item.image}
             />
             <Card.Body>
               <Card.Title>{item.name}</Card.Title>
               <Card.Text>
                 <h4 style={{display:'flex',justifyContent:'space-between'}}>Price<span>{item.price}</span></h4>
               </Card.Text>
             </Card.Body>
             <Card.Footer>
               <Button style={{float:'right'}}
                onClick={()=>addToCart(item.name,item.price)}
               >Add to Cart</Button>
             </Card.Footer>
           </Card>
          </Col>
                  )
                })}

              </Row>
              </Col>
              <Col xs={3}>
                <Card>
                  <Card.Title><h3 style={{textAlign:'center'}}>Cart</h3></Card.Title>
                  <Card.Body>
                  <Card.Text>
                {cart ? cart.map((item)=>
                {
                   return (
                      <div>
                        <h5 style={{display:'flex',justifyContent:'space-between'}}>
                          {item.name}<span>Rs.{item.price}</span></h5></div>
                   )
                }):<h3>Your Cart Is Empty!</h3> }   
                  </Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <h4 style={{display:'flex',justifyContent:'space-between'}}>Total:<span>Rs.{total}</span></h4>
                    <br/>
                    <Button style={{float:'right'}}
                    onClick={()=>pay()}
                    >Proceed to Pay</Button>
                  </Card.Footer>
                </Card>
              </Col>
          </Row>
        </Container>
    </>
  );
};

export default Cafepage;
